
(function($){$.fn.stickyheader=function(options){var defaults={hidden:false};var o=jQuery.extend(defaults,options);return this.each(function(){var sh=$(this);createMinimizeControl(sh);createMaximizeContol(sh);sh.minimizeControl=sh.children('#minimize');sh.maximizeControl=sh.children('#maximize');sh.fullHeight=sh.outerHeight();sh.maximizeControlTopPos=sh.maximizeControl.css('top');sh.minimizeControl.bind('click',sh,minimizeClick);sh.maximizeControl.bind('click',sh,maximizeClick);});};function createMinimizeControl(sh){sh.append('<div id="minimize"></div>');}
function createMaximizeContol(sh){sh.append('<div id="maximize"></div>');}
function showMaximizeControl(sh){sh.maximizeControl.animate({'top':sh.fullHeight+'px'});}
function hiddenMaximizeControl(sh){sh.maximizeControl.animate({'top':sh.maximizeControlTopPos});}
function showStickyHeader(sh){sh.animate({'top':'0px'});}
function hiddenStickyHeader(sh){sh.animate({'top':'-'+sh.fullHeight+'px'});}
function minimizeClick(event){var $this=$(this);var sh=event.data;hiddenStickyHeader(sh);showMaximizeControl(sh);}
function maximizeClick(event){var $this=$(this);var sh=event.data;hiddenMaximizeControl(sh);showStickyHeader(sh);}})(jQuery);